import {useEffect} from 'react'
import * as THREE from 'three';
import texture1 from './circle.png'
import Stats from './Stats.js'

export default function Threejs(){

useEffect(() => {

	//GET PERFORMACE STATS, COMMENT TO HIDE
	(function(){var script=document.createElement('script');script.onload=function(){var stats=new Stats();document.body.appendChild(stats.dom);requestAnimationFrame(function loop(){stats.update();requestAnimationFrame(loop)});};script.src='//mrdoob.github.io/stats.js/build/stats.min.js';document.head.appendChild(script);})()


		// Global Variables
		let sizes = {
			width: window.innerWidth,
			height: window.innerHeight
		}

		let mouseX = 0;
		let mouseY = 0;

		// CREATE SCENE
		const scene = new THREE.Scene();

		const geometry = new THREE.PlaneGeometry( 1, 1 );
		const material = new THREE.ShaderMaterial({
			uniforms: {
				u_time: { value: 1.0 },
				u_resolution: { value: new THREE.Vector2(window.innerWidth, window.innerHeight) },
				u_mouse: {value: new THREE.Vector2(mouseX, mouseY)},
			},
			vertexShader: document.getElementById( 'vertexShader' ).textContent,
			fragmentShader: document.getElementById( 'fragmentShader' ).textContent
		});
		const plane = new THREE.Mesh( geometry, material );
		scene.add( plane );



		// CAMERA
        const fieldOfView = 75;
        const aspectRatio = sizes.width / sizes.height;
        const nearPlane = 1;
		const farPlane = 3;

		const camera = new THREE.PerspectiveCamera(fieldOfView, aspectRatio, nearPlane, farPlane);
		camera.position.x = 0;
		camera.position.y = 0;
		camera.position.z = farPlane/3;
		scene.add(camera)

		// FRONT-END PART
		let container = document.getElementById('webgl-canvas');
		container.style.width = '100%';
		container.style.height = '100vh';
		container.style.overflow = 'hidden';

		// RENDERER
		const renderer = new THREE.WebGLRenderer(); 
		renderer.setPixelRatio(window.devicePixelRatio); 	
		renderer.setSize(sizes.width, sizes.height); 
		container.appendChild(renderer.domElement); 

		function render() {
			renderer.render(scene, camera);
			//define shader time
			material.uniforms.time.value += 0.04;
		}

		function animate() {
			requestAnimationFrame(animate);
			render();
		}
		animate();

		// EVENT LISTENERS
		window.addEventListener('resize', onWindowResize, false);

		let windowHalfX = sizes.width / 2;
		let windowHalfY = sizes.height / 2;

		function onWindowResize() {
			windowHalfX = window.innerWidth / 2;
			windowHalfY = window.innerHeight / 2;

			camera.aspect = window.innerWidth / window.innerHeight;
			camera.updateProjectionMatrix();
			renderer.setSize(window.innerWidth, window.innerHeight);
		}

      },[]);

return(
   null
)

}
