import {React, useEffect} from 'react'
import './Info.css'

export default function Information1(props){

    //first effect for when component mounts
    useEffect(()=>{
        let container = document.getElementById('info1-container');
        let info1 = document.getElementById('info1-1');
        let info2 = document.getElementById('info1-2');
        let rect = container.getBoundingClientRect();

        if (window.pageYOffset >= rect.top && window.pageYOffset <= rect.top + container.clientHeight){
            info1.classList.remove('info1-1-start');
            info1.classList.add('info1-1');
            info2.classList.remove('info1-2-start');
            info2.classList.add('info1-2');
        } 
    },[]);

    //effect for during page navigation
    useEffect(()=>{

        window.addEventListener('scroll', scrollAnimate);

        function scrollAnimate(){
            let container = document.getElementById('info1-container');
            let info1 = document.getElementById('info1-1');
            let info2 = document.getElementById('info1-2');

            let rect = container.getBoundingClientRect();

            if (window.pageYOffset >= rect.top && window.pageYOffset <= rect.top + container.clientHeight){
                info1.classList.remove('info1-1-start');
                info1.classList.add('info1-1');
                info2.classList.remove('info1-2-start');
                info2.classList.add('info1-2');
            } else {
                info1.classList.remove('info1-1');
                info1.classList.add('info1-1-start');
                info2.classList.remove('info1-2');
                info2.classList.add('info1-2-start');
            }
          }


    return ()=>window.removeEventListener('scroll',scrollAnimate);
    })

    return(

    <div id ='info1-container' className="info1-container">

        <div id = 'info1-1' className ="info1-1-start">
            <h1 className="heading">{props.heading}</h1>
            <p className="text">{props.text}</p>
        </div>

        <div id = "info1-2" className ="info1-2-start">
            <p className="text">We are a digital studio from Porto, Portugal interested in creating digital and interactive experiences for brands, individuals and the arts in general.</p>
        </div>

    </div>

   )
 }
