import {React, useEffect} from 'react'
import './Info.css'

export default function Information1(props){

    useEffect(()=>{

        window.addEventListener('scroll', scrollAnimate);

        function scrollAnimate(e){
            let container = document.querySelectorAll('.info-container');
            let info1 = document.getElementById('info-1');
            let info2 = document.getElementById('info-2');

            let rect;
            for(var i = 0; i<container.length; i++){
                rect =  container[i].getBoundingClientRect();


            console.log(window.pageYOffset);
            console.log(rect.top*-1);

            if (window.pageYOffset >= rect[i].top*(-1)){
                info1.classList.remove('info-1-start');
                info1.classList.add('info-1');
                info2.classList.remove('info-2-start');
                info2.classList.add('info-2');
            } else {
                info1.classList.remove('info-1');
                info1.classList.add('info-1-start');
                info2.classList.remove('info-2');
                info2.classList.add('info-2-start');
            }
        }
        }

    return ()=>window.removeEventListener('scroll',scrollAnimate);
    })

    return(

    <div id ='info-container' className="info-container">

        <div id = 'info-1' className ="info-1-start">
            <h1 className="heading">{props.heading}</h1>
            <p className="text">{props.text}</p>
        </div>

        <div id = "info-2" className ="info-2-start">
            <p className="text">We are a digital studio from Porto, Portugal interested in creating digital and interactive experiences for brands, individuals and the arts in general.</p>
        </div>

    </div>

   )
 }
