import {React, useEffect} from 'react'
import './Info.css'

export default function Information2(){

    useEffect(()=>{

        window.addEventListener('scroll', scrollAnimate);

        function scrollAnimate(e){
            let container2 = document.getElementById('info-container-2');
            let info1 = document.getElementById('info-1');
            let info2 = document.getElementById('info-2');


            console.log(window.pageYOffset);
            console.log(container2.clientTop);

            if (window.pageYOffset >= container2.clientTop && window.pageYOffset <= container2.clientHeight/3){
                info1.classList.remove('info-1-start');
                info1.classList.add('info-1');
                info2.classList.remove('info-2-start');
                info2.classList.add('info-2');
            } else {
                info1.classList.remove('info-1');
                info1.classList.add('info-1-start');
                info2.classList.remove('info-2');
                info2.classList.add('info-2-start');
        }
    }

    return ()=>window.removeEventListener('scroll',scrollAnimate);
    })

   return(

    <div id = "info-container-2" className="info-container">

        <div id = "info-1" className ="info-1-start">
            <h1 className="heading">Our beliefs</h1>
            <p className="text">We believe in creative thinking and the power of exploration, in going beyond our restrictive habits by tearing apart everything that is taken for granted and taught as absolute truth.</p>
            <p className="text">We believe in the power of communication and feedback, the power of the group over the individual. In every project we try to go beyond the standards. </p>
        </div>

    </div>

   )
 }
