import {React, useEffect} from 'react'
import './Info.css'

export default function Information3(){

    useEffect(()=>{

        window.addEventListener('scroll', scrollAnimate);

        function scrollAnimate(){

            let container = document.getElementById('info3-container');
            let rect = container.getBoundingClientRect();

            let info1 = document.getElementById('info3-1');

            console.log(rect.top);
            console.log(window.pageYOffset);
            console.log(window.pageYOffset>=2*rect.top);

            if (window.pageYOffset >= 2*rect.top && window.pageYOffset <= rect.top + 3*container.clientHeight){
                info1.classList.remove('info3-1-start');
                info1.classList.add('info3-1');
            } else {
                info1.classList.remove('info3-1');
                info1.classList.add('info3-1-start');
            }
          }

        return ()=>window.removeEventListener('scroll',scrollAnimate);
    })

   return(

    <div id = "info3-container" className="info3-container">
        <div className="info3-wrapper">
        <div id = "info3-1" className ="info3-1-start">
        <p className="text">We believe in the power of communication and feedback, the power of the group over the individual. In every project we try to go beyond the standards. </p>
        </div>
        </div>
    </div>

   )
 }
