import {React, useEffect} from 'react'
import './Info.css'

export default function Information3(){

    useEffect(()=>{

        window.addEventListener('scroll', scrollAnimate);

        function scrollAnimate(e){

            let container = document.getElementById('info3-container');
            let info1 = document.getElementById('info3-1');
            let info2 = document.getElementById('info3-2');
            let rect = container.getBoundingClientRect();
            console.log(e);

            if (window.pageYOffset >= rect.top && window.pageYOffset <= rect.top + 3*container.clientHeight){
                info1.classList.remove('info3-1-start');
                info1.classList.add('info3-1');
                info2.classList.remove('info3-2-start');
                info2.classList.add('info3-2');
            } else {
                info1.classList.remove('info3-1');
                info1.classList.add('info3-1-start');
                info2.classList.remove('info3-2');
                info2.classList.add('info3-2-start');
            }
          }

        return ()=>window.removeEventListener('scroll',scrollAnimate);
    })


   return(

    <div id = "inf3-container" className="info3-container">

        <div id = "info3-1" className ="info3-1-start">
            <h1 className="heading">Who we are</h1>
            <p className="text">It's easy to create a standard: it's a precautionary measure, a safety valve, a shield against devastating, subversive genius. Art however is an invincible force. It opens up your inner self completely, because creativity will only nurture yourself</p>
        </div>

        <div id = "info3-2" className ="info3-2-start">
            <h1 className="heading">Contacts</h1>
            <p >We are a digital studio from Porto, Portugal interested in creating digital and interactive experiences</p>
        </div>

    </div>

   )
 }
