import {React, useEffect} from 'react'
import * as THREE from 'three';

export default function Threejs(){

useEffect(() => {

		var OrbitControls = require('three-orbit-controls')(THREE)

		var scene, camera, renderer;

		// I guess we need this stuff too
		var container, HEIGHT,
			WIDTH, fieldOfView, aspectRatio,
			nearPlane, farPlane, particleCount,
			i,
			mouseX = 0,
			mouseY = 0,
			windowHalfX, windowHalfY, cameraZ;

		let controls
		let points;
		points = [];
		particleCount = 2000;

		init();
		animate();

		function init() {

			const sizes = {
				width: window.innerWidth,
				height: window.innerHeight
			}

          windowHalfX = WIDTH / 2;
          windowHalfY = HEIGHT / 2;

        fieldOfView = 75;
        aspectRatio = sizes.width / sizes.height;
        nearPlane = 1;
		farPlane = 3000;

		cameraZ = farPlane / 3; /*	So, 1000? Yes! move on!	*/


        camera = new THREE.PerspectiveCamera(fieldOfView, aspectRatio, nearPlane, farPlane);
        camera.position.z = cameraZ;

        scene = new THREE.Scene();


        container = document.createElement('div');
        document.body.appendChild(container);
        document.body.style.margin = 0;
        document.body.style.overflow = 'hidden';

			//camera controls
			controls = new OrbitControls(camera)

			for (i = 0; i < particleCount; i++) {

				var vertex = new THREE.Vector3();
				vertex.x = Math.random() * 2000 - 1000;
				vertex.y = Math.random() * 2000 - 1000;
				vertex.z = Math.random() * 2000 - 1000;
				points.push(vertex);
			}

			//define geometry
			const geometry = new THREE.BufferGeometry().setFromPoints( points );
			const geometry2 = new THREE.TorusGeometry(100,100,10,100);

			let uniforms = {
				u_size: { value: 10.0 }
			};

			//import texture
			const loader = new THREE.TextureLoader();
			const circle = loader.load('./circle.png');

			//define material
			const material = new THREE.PointsMaterial( {
				size:10,
				//map: circle, 
				transparent:true

			 } );

			const shaderMaterial = new THREE.ShaderMaterial( {

				uniforms: uniforms,
				vertexShader: document.getElementById( 'vertexshader' ).textContent,
				fragmentShader: document.getElementById( 'fragmentshader' ).textContent,

			} );

			//define mesh
			const mesh = new THREE.Points( geometry, material );
			mesh.scale.set( 1, 1, 1 );

			const mesh2 = new THREE.Points( geometry2, material );
			mesh2.scale.set( 1, 1, 1 );

			scene.add(mesh);

			renderer = new THREE.WebGLRenderer(); /*	Rendererererers particles.	*/
			renderer.setPixelRatio(window.devicePixelRatio); /*	Probably 1; unless you're fancy.	*/
			renderer.setSize(sizes.width, sizes.height); /*	Full screen baby Wooooo!	*/

			container.appendChild(renderer.domElement); /* Let's add all this crazy junk to the page.	*/

			/*	If my calculations are correct, when this baby hits 88 miles per hour...
		you're gonna see some serious shit.	*/


			/* Event Listeners */
			window.addEventListener('resize', onWindowResize, false);
			document.addEventListener('mousemove', onDocumentMouseMove, false);
			document.addEventListener('touchstart', onDocumentTouchStart, false);
			document.addEventListener('touchmove', onDocumentTouchMove, false);

		}

		function animate() {
			requestAnimationFrame(animate);
			render();
		}


		function render() {
			var time = Date.now() * 0.5;

			camera.position.x += (mouseX - camera.position.x) * 0.05;
			camera.position.y += (-mouseY - camera.position.y) * 0.05;
			camera.lookAt(scene.position);

			renderer.render(scene, camera);
		}

		function onDocumentMouseMove(e) {
			mouseX = e.clientX - windowHalfX;
			mouseY = e.clientY - windowHalfY;
		}

		/*	Mobile users?  I got your back homey	*/

		function onDocumentTouchStart(e) {

			if (e.touches.length === 1) {
				e.preventDefault();
				mouseX = e.touches[0].pageX - windowHalfX;
				mouseY = e.touches[0].pageY - windowHalfY;
			}
		}

		function onDocumentTouchMove(e) {

			if (e.touches.length === 1) {
				e.preventDefault();
				mouseX = e.touches[0].pageX - windowHalfX;
				mouseY = e.touches[0].pageY - windowHalfY;
			}
		}

		function onWindowResize() {

			windowHalfX = window.innerWidth / 2;
			windowHalfY = window.innerHeight / 2;

			camera.aspect = window.innerWidth / window.innerHeight;
			camera.updateProjectionMatrix();
			renderer.setSize(window.innerWidth, window.innerHeight);
		}

      },[]);

return(
