import {React, useEffect} from 'react'
import './Info.css'

export default function Information1(){

    useEffect(()=>{

        window.addEventListener('scroll', scrollAnimate);

        function scrollAnimate(e){
            let container = document.getElementById('info-container');
            let element = document.getElementById('info-1');

            console.log(window.pageYOffset);
            console.log(container.clientTop);


            if (window.pageYOffset >= container.clientTop + 100){
                element.classList.add('info-1');
        } else {
            element.classList.remove('info-1-start');
        }
    }

    return ()=>window.removeEventListener('scroll',scrollAnimate);
    },[])

   return(

    <div className="info-container">

        <div className ="info-1-start">
            <h1 className="heading">Who we are</h1>
            <p className="text">It's easy to create a standard: it's a precautionary measure, a safety valve, a shield against devastating, subversive genius. Art however is an invincible force. It opens up your inner self completely, because creativity nurtures the soul.</p>
        </div>

        <div className ="info-2">
            <p className="text">We are a digital studio from Porto, Portugal interested in creating digital and interactive experiences for brands, individuals and the arts in general.</p>
        </div>

    </div>

   )
 }
