import {useEffect} from 'react'
import * as THREE from 'three';
import Stats from './Stats.js'

export default function Threejs(){

const vertexShader = `
precision mediump float;
uniform float u_time;

void main(){
  gl_Position = vec4(position, 1.0);
}
`

const fragmentShader = `
precision mediump float;
uniform vec2 u_resolution;
uniform vec2 u_mouse;
uniform float u_time;

void main() {
  vec2 st = 10.0+gl_FragCoord.xy/u_resolution;
  vec2 coord = st*17.0;
  float len;

  for (float i = 0.5; i<1.0; i+=0.1){
	coord.x +=  0.5*sin(coord.y*i +u_time);
	coord.y +=  0.5*sin(coord.x*i + u_time * 0.5);
  }

  len = length(vec2(coord.x, coord.y));
  vec3 color = vec3(fract(len*0.1),sin(len*0.5),fract(len*0.3));
  gl_FragColor = vec4(color, 1.0);
}
`

useEffect(() => {

	//GET PERFORMACE STATS, COMMENT TO HIDE
	(function(){var script=document.createElement('script');script.onload=function(){var stats=new Stats();document.body.appendChild(stats.dom);requestAnimationFrame(function loop(){stats.update();requestAnimationFrame(loop)});};script.src='//mrdoob.github.io/stats.js/build/stats.min.js';document.head.appendChild(script);})()

	// (FRONT-END) CHOOSE SHADER CONTAINER 
	let container = document.getElementById('webgl-canvas');

	//DEFINE SIZE
	let sizes = {
		width: window.innerWidth,
		height: window.innerHeight
	}

    //CREATE SHADER CANVAS
	const renderer = new THREE.WebGLRenderer(); 
	renderer.setPixelRatio(window.devicePixelRatio); 	
	renderer.setSize(sizes.width, sizes.height); 
	container.appendChild(renderer.domElement); 

	// CREATE SCENE
	const scene = new THREE.Scene();

	// CREATE GEOMETRY
	const geometry = new THREE.SphereGeometry( 5, 20, 20);

	// CREATE CUSTOM MATERIAL


	const material = new THREE.MeshBasicMaterial( {color: 0xffff00} );

	// CREATE MESH
	const mesh = new THREE.Mesh( geometry, material );
	scene.add(mesh);

	// CREATE CAMERA
	const camera = new THREE.PerspectiveCamera(45, sizes.width / sizes.height, 1, 1000 );
	scene.add(camera)

	// 
	function render() {
		renderer.render(scene, camera);
		//SHADER UNIFORMS
		//material.uniforms.u_time.value += 0.04;
	}

	function animate() {
		requestAnimationFrame(animate);
		render();
	}
	animate();

	// EVENT LISTENERS
	window.addEventListener('resize', onWindowResize, false);

	// WINDOW RESIZE
	function onWindowResize() {
		camera.aspect = window.innerWidth / window.innerHeight;
		camera.updateProjectionMatrix();
		renderer.setSize(window.innerWidth, window.innerHeight);
	}

	},[]);

return(
   null
)
}
