import {useEffect} from 'react'
import * as THREE from 'three';
import texture1 from './circle.png'

export default function Threejs(){

useEffect(() => {

		// Global Variables
		let sizes = {
			width: window.innerWidth,
			height: window.innerHeight
		}


		// CREATE SCENE
		const scene = new THREE.Scene();

		// CREATE GEOMETRY FROM CUSTOM POINTS
		const geometry = new THREE.SphereGeometry(300,50,50);
		geometry.computeVertexNormals();
		geometry.scale( 0.5, 0.5, 0.5 );

		// MATERIAL WITH IMPORTED TEXTURE
		const material = new THREE.MeshNormalMaterial();
		material.color = 'white';

		// MESH
		const amount = 10;
		const count = Math.pow( amount, 3 );
		const mesh = new THREE.InstancedMesh( geometry, material,count);
		//mesh.instanceMatrix.setUsage( THREE.DynamicDrawUsage );
		scene.add(mesh);

		// LIGHTS
		const pointLight = new THREE.PointLight(0xffffff, 0.1)
		pointLight.position.x = 2
		pointLight.position.y = 3
		pointLight.position.z = 1000
		scene.add(pointLight)

		// CAMERA
        const fieldOfView = 75;
        const aspectRatio = sizes.width / sizes.height;
        const nearPlane = 1;
		const farPlane = 3000;

		const camera = new THREE.PerspectiveCamera(fieldOfView, aspectRatio, nearPlane, farPlane);
		camera.position.x = 0;
		camera.position.y = 0;
		camera.position.z = farPlane/3;
		scene.add(camera)

		// ADD CAMERA CONTROLS
		const OrbitControls = require('three-orbit-controls')(THREE)
        const controls = new OrbitControls(camera)

		// FRONT-END PART
		let container = document.getElementById('webgl-canvas');
		container.style.width = '100%';
		container.style.height = '100vh';
		container.style.overflow = 'hidden';

		// RENDERER
		const renderer = new THREE.WebGLRenderer(); /*	Rendererererers particles.	*/
		renderer.setPixelRatio(window.devicePixelRatio); /*	Probably 1; unless you're fancy.	*/
		renderer.setSize(sizes.width, sizes.height); /*	Full screen baby Wooooo!	*/
		container.appendChild(renderer.domElement); /* Let's add all this crazy junk to the page.	*/

		let mouseX = 0;
		let mouseY = 0;
		function render() {
			//camera.position.x += (mouseX - camera.position.x) * 0.05;
			//camera.position.y += (-mouseY - camera.position.y) * 0.05;
			camera.lookAt(scene.position);
			renderer.render(scene, camera);
		}

		function animate() {
			requestAnimationFrame(animate);
			render();
		}
		animate();

		// EVENT LISTENERS
		window.addEventListener('resize', onWindowResize, false);
	/* 	document.addEventListener('mousemove', onDocumentMouseMove, false);
		document.addEventListener('touchstart', onDocumentTouchStart, false);
		document.addEventListener('touchmove', onDocumentTouchMove, false);

		let windowHalfX = sizes.width / 2;
		let windowHalfY = sizes.height / 2;
		function onDocumentMouseMove(e) {
			mouseX = e.clientX - windowHalfX;
			mouseY = e.clientY - windowHalfY;
		}

		function onDocumentTouchStart(e) {
			if (e.touches.length === 1) {
				e.preventDefault();
				mouseX = e.touches[0].pageX - windowHalfX;
				mouseY = e.touches[0].pageY - windowHalfY;
			}
		}

		function onDocumentTouchMove(e) {
			if (e.touches.length === 1) {
				e.preventDefault();
				mouseX = e.touches[0].pageX - windowHalfX;
				mouseY = e.touches[0].pageY - windowHalfY;
			}
		} */

		function onWindowResize() {
			//windowHalfX = window.innerWidth / 2;
			//windowHalfY = window.innerHeight / 2;

			camera.aspect = window.innerWidth / window.innerHeight;
			camera.updateProjectionMatrix();
			renderer.setSize(window.innerWidth, window.innerHeight);
		}

      },[]);

return(
   null
)

}
