import {React, useEffect} from 'react'
import './Info.css'

export default function Information2(){

    useEffect(()=>{

        window.addEventListener('scroll', scrollAnimate);

        function scrollAnimate(){

            let container = document.getElementById('info2-container');
            let rect = container.getBoundingClientRect();
            let info1 = document.getElementById('info2-1');

            if (window.pageYOffset >= rect.top && window.pageYOffset <= rect.top + 2*container.clientHeight){
                info1.classList.remove('info2-1-start');
                info1.classList.add('info2-1');
            } else {
                info1.classList.remove('info2-1');
                info1.classList.add('info2-1-start');
            }
          }

        return ()=>window.removeEventListener('scroll',scrollAnimate);
    })

   return(

    <div id = "info2-container" className="info2-container">
        <div className="info2-wrapper">
        <div id = "info2-1" className ="info2-1-start">
            <h1 className="heading">Our beliefs</h1>
            <p className="text">We believe in creative thinking and the power of exploration, in going beyond our restrictive habits by tearing apart everything that is taken for granted and taught as absolute truth.</p>
            <p className="text">We believe in the power of communication and feedback, the power of the group over the individual. In every project we try to go beyond the standards. </p>
        </div>
        </div>
    </div>

   )
 }
